mapper.update:
update czp_message
set `status`=1
where (`from_id` = #{u2} and `to_id` = #{u1} and `status` = 0);

mapper.select:
select u.* from czp_user u
    left join chat_list cl on  cl.other_id=u.id
where cl.own_id=#{id}

select m.* ,u.avatar,u.`name`  from czp_message m
    left join czp_user u on u.id=m.from_id
where m.to_id=#{toId}
